// continue socketio
import { Button, Text, TextInput } from 'react-native-paper';
import { StyleSheet, View } from 'react-native';
import { Icon } from 'react-native-vector-icons/MaterialCommunityIcons';
import { useContext, useEffect, useState } from 'react';
import { AppContext, AppContextProvider } from '../scripts/AppContext'
import { connectSocketIO, onUsersCountReceive, broadcastTextMessage } from '../scripts/Socket'

export default function Home() {
    const [messageText, setMessageText] = useState("")
    
	const appContext = useContext(AppContext)
	onUsersCountReceive((newUsersCount) => {
		appContext.setUsersCount(newUsersCount)
	})

    const onMessageTextChange = (event) => {
        console.log("Changed Text:", event.target.value)
        return
    }

    return <View>
        <View style={{padding: 32, display: "flex", justifyContent: "center", alignItems: "center"}}>
            <Text variant='displaySmall'>Hello World</Text>
            <Text style={{textAlign: "center"}}>You are <Text style={{fontWeight: "bold"}}>{appContext.clientName}</Text>. {appContext.usersCount} users online.</Text>
        </View>
        <TextInput value={messageText} label={"Enter Message to broadcast!"} onChangeText={setMessageText} onSubmitEditing={() => broadcastTextMessage(messageText)}/>
    </View>
}
