import { useState, createContext } from 'react';
import { MD3LightTheme, MD3DarkTheme } from 'react-native-paper';
import { getUniqueId, getModel, getBaseOs} from 'react-native-device-info'

const AppContext = createContext()

function AppContextProvider(props) {
    const [onlineUsers, setOnlineUsers] = useState(-1);
    const clientName = [getModel(), getBaseOs(), getUniqueId(), "BrowserClient " + btoa(Math.random())].filter(a => !['unknown', {}].includes(a) && a.length > 0)[0]
	const [darkTheme, setDarkTheme] = useState(true)
    const themes = {
        // Set status bar color in these themes
        light: {
            ...MD3LightTheme,
            mode: "adaptive",
            colors: {
                ...MD3LightTheme.colors,
            }
        },      
        dark: {
            ...MD3DarkTheme,
            mode: "adaptive",
            colors: {
                ...MD3DarkTheme.colors,
            }
        },
        current: () => darkTheme ? themes.dark : themes.light
    }

    return <AppContext.Provider value={{
        onlineUsers,
        setOnlineUsers,
        clientName,
        darkTheme,
        setDarkTheme,
        themes
    }}>
        {props.children}
    </AppContext.Provider>
}

export {
    AppContextProvider,
    AppContext
}
