// continue socketio
import { Button, Text } from 'react-native-paper';
import { StyleSheet, View } from 'react-native';
import { Icon } from 'react-native-vector-icons/MaterialCommunityIcons';
import { useContext, useEffect, useState } from 'react';

import { AppContext } from '../AppContext'

export default function Home() {
    const { clientName, usersCount, setUsersCount } = useContext(AppContext)

    useEffect(() => {
        setUsersCount(0)
    }, [setUsersCount])

    useEffect(() => {
        console.log(usersCount)
    }, [usersCount])


    return <View style={{padding: 32, display: "flex", justifyContent: "center", alignItems: "center"}}>
        <Text variant='displaySmall'>Hello World</Text>
        <Text style={{textAlign: "center"}}>You are <Text style={{fontWeight: "bold"}}>{clientName}</Text>. {usersCount} users online.</Text>
    </View>
}
