import { useState, createContext } from 'react';

const { Provider, Consumer } = createContext()

function AppContext(props) {
    const [stuff, setStuff] = useState('foo');
	const [darkTheme, setDarkTheme] = useState(true)

    return <Provider value={{
        stuff,
        setStuff,
        darkTheme,
        setDarkTheme,
    }}>
        {props.children}
    </Provider>
}

export {
    AppContext,
    Consumer
}
