import { StatusBar } from 'expo-status-bar';
import { Appbar, BottomNavigation, Button, Text, TouchableRipple } from 'react-native-paper';
import { SafeAreaView, ScrollView, StyleSheet, View } from 'react-native';
import { useContext, useEffect, useState } from 'react';

import Home from './Home'
import More from './More'
import { AppContext, AppContextProvider } from '../AppContext'

import { io } from 'socket.io-client';

let appContext

const HomeContainer = () => <><Home context={appContext}/></>
const MoreContainer = () => <><More context={appContext}/></>

export default function App(props) {
	appContext = useContext(AppContext)

	const [index, setIndex] = useState(0);
	const [routes] = useState([
		{ key: 'home', title: 'Home', focusedIcon: 'home', unfocusedIcon: 'home-outline'},
		{ key: 'more', title: 'Settings', focusedIcon: 'cog', unfocusedIcon: 'cog-outline' },
	]);

	const renderScene = BottomNavigation.SceneMap({
		home: HomeContainer,
		more: MoreContainer,
	})

	const [loading, setLoading] = useState('ready')

	let socket
	useEffect(() => {
		console.log(appContext.usersCount)
	}, [appContext.usersCount])

    const connectSocketIO = () => {
        socket = io("http://192.168.1.7:4000/")

		setLoading('loading')
        socket.on('connect', () => setLoading('connected'))
		socket.emit('introduction', appContext.clientName)
        socket.on('reconnect_failed', () => setLoading('fail'))
		socket.on('disconnect', () => setLoading('false'))

		socket.on('connected-users', onUsersCountReceive)
    }

	const onUsersCountReceive = (newUsersCount) => {
		appContext.setUsersCount(newUsersCount)
	}

	if (loading == 'ready') connectSocketIO()

	return (
			<AppContextProvider>
				<SafeAreaView/>
				<Appbar.Header style={{marginTop: StatusBar.currentHeight}} dark={true}>
					<Appbar.Action icon="chess-queen" />
					<Appbar.Content title={"Total Users: " + appContext.usersCount} />
					<Button type="text" icon={{connected: "check", fail: "warning", false: "power-plug"}[loading]} loading={loading == 'loading'} disabled={['loading', 'connected'].includes(loading)} onPress={() => { setLoading('ready'); }}>
						{{fail: "Failed to Connect. Tap to retry", false: "Disconnected. Tap to connect", loading: "Connecting...", connected: "Connected!"}[loading]}
					</Button>
					<Appbar.Action icon="brightness-6" onPress={props.changeTheme} />
				</Appbar.Header>
				<BottomNavigation
					navigationState={{ index, routes }}
					onIndexChange={setIndex}
					renderScene={renderScene}
				/>
			</AppContextProvider>
	);
}

